import i18next from "i18next";
import Backend from "i18next-http-backend"
import LanguageDetector from "i18next-browser-languagedetector"; // Исправлено: Languagedetector -> LanguageDetector
import { initReactI18next } from "react-i18next";

i18n
    .use(Backend)
    .use(LanguageDetector) // Исправлено: Languagedetector -> LanguageDetector
    .use(initReactI18next)
    .init({
        fallbackLng: "eng",
        whitelist: ["eng", "rus"], // Исправлено: whielist -> whitelist
        debug: false,
        detection: {
            order: ["localStorage", "cookie"], // Исправлено: caches -> cache
            cache: ["localStorage", "cookie"], // Исправлено: caches -> cache
        },
        interpolation: {
            escapeValue: false,
        },
    });

export default i18n;
